mapper.insert:

INSERT INTO review (title, content, author_id, rating, img, product_info, order_id)
VALUES (#{title}, #{content}, #{authorId}, #{rating}, #{img}, #{productName}, #{orderId});


UPDATE completeproductpay
SET review = 1
WHERE orderId = #{orderId};

mapper.select:
